import * as React from 'react';
import { useState } from 'react';
import { firebase } from '../firebase.js';
import { View, Text, Image, TouchableOpacity } from 'react-native';

export default function Card(props) {
    const [productName, setProductName] = useState('')
    const [productPrice, setPrice] = useState('')
    const [productAge, setAge] = useState('')
    const [productImageUrl, setImageUrl] = useState('')

    let user;
    const usersRef = firebase.firestore().collection('products')
    usersRef
        .orderBy('productName', 'desc')
        .get()
        .then(firestoreDocument => {
            if (!firestoreDocument.exists) {
                alert("User does not exist anymore.")
                return;
            }
            user = firestoreDocument.data()
            setProductName(user.productName)
            setPrice(user.productPrice)
            setAge(user.productAge)
            setImageUrl(user.productImageUrl)
        })
        .catch(error => {
            alert(error)
        });

    console.log()

    return (
        <View style={{ flex: 1, alignItems: "center", justifyContent: "center", backgroundColor: "rgba(0,0,0,0.1)", height: 250, margin: 20, width: '100%' }}>
            <View style={{ flex: 5, width: '100%' }}>
                <TouchableOpacity onPress={() => props.param.navigate('Product')} style={{ width: '100%', flex: 1 }}>
                    <Image
                        source={require('../images/urun1.jpg')}
                        style={{
                            width: '100%',
                            height: '100%',
                        }}
                    />
                </TouchableOpacity>
            </View>
            <View style={{ flex: 4, alignItems: "center", width: '100%' }}>
                <View style={{ flex: 3, justifyContent: 'center' }}>
                    <Text style={{ fontSize: 20, fontWeight: 'bold' }}>76 ₺</Text>
                </View>
                <View style={{ flex: 2, justifyContent: 'center' }}>
                    <Text style={{ fontSize: 15, fontWeight: 'bold' }}>ERKEK 4'LÜ TAKIM </Text>
                </View>
                <View style={{ flex: 2, flexDirection: 'row', justifyContent: 'space-between', alignItems: "center", width: '100%' }}>

                    <View style={{ marginLeft: 8 }}>
                        <Text style={{ fontSize: 15 }}>4-8 YAŞ</Text>
                    </View>
                    <View>
                        <TouchableOpacity>
                            <Image
                                source={require('../images/sepetAdd.png')}
                                style={{
                                    width: 35,
                                    height: 35,
                                    margin: 8
                                }}
                            />
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        </View>
    )
}
