import React, { useState } from 'react'
import { View, Text, StyleSheet, TextInput, Dimensions, TouchableOpacity } from 'react-native';
import { firebase } from '../firebase.js';
import Home from '../screens/Home.js';
import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

function StackTemplate(navigation, screen) {
  return (
    <Stack.Navigator initialRouteName="FirstPage">
      <Stack.Screen
        name="FirstPage"
        component={screen}        
        options={{
          title: 'Triliya', //Set Header Title

          headerLeft: () =>
            <NavigationDrawerStructure
              navigationProps={navigation}
            />,
          headerRight: () =>
            <NavigationDrawerOrder
              navigationProps={navigation}
            />,

          headerStyle: {
            backgroundColor: '#4bae32', //Set Header color
          },
          headerTintColor: '#fff', //Set Header text color
          headerTitleStyle: {
            fontWeight: 'bold', //Set Header text style
          },
        }}
      />
    </Stack.Navigator>
  );
}



export default function Login({navigation}) {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')


  const girisyap = () => {
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((response) => {
        const uid = response.user.uid
        const usersRef = firebase.firestore().collection('users')
        usersRef
          .doc(uid)
          .get()
          .then(firestoreDocument => {
            if (!firestoreDocument.exists) {
              alert("User does not exist anymore.")
              return;
            }
            const user = firestoreDocument.data()
            StackTemplate(navigation, Home)
          })
          .catch(error => {
            alert(error)
          });
      })
      .catch(error => {
        alert(error)
      })
  }

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <TextInput
        placeholder="E-mail Adresi"
        style={{ marginTop: 10, width: '80%', padding: 10, fontSize: 12, backgroundColor: 'white', borderRadius: 4 }}
        underlineColorAndroid='transparent'
        onChangeText={(text) => setEmail(text)}
        keyboardType='email-address'
        placeholderTextColor='gray'
      />

      <TextInput
        placeholder="Şifre"
        style={{ marginTop: 10, width: '80%', padding: 10, fontSize: 12, backgroundColor: 'white', borderRadius: 4 }}
        underlineColorAndroid='transparent'
        onChangeText={(text) => setPassword(text)}
        secureTextEntry
        placeholderTextColor='gray'
      />
      <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
        <View style={{ alignItems: 'center', backgroundColor: '#FF655B', width: '80%', padding: 15, borderRadius: 4, marginTop: 10 }}>
          <Text style={{ color: '#fff', fontSize: 12 }}>Kayıt Ol</Text>
        </View>
      </TouchableOpacity>

      <TouchableOpacity onPress={() => girisyap()}>
        <View style={{ alignItems: 'center', backgroundColor: '#FF655B', width: '80%', padding: 15, borderRadius: 4, marginTop: 10 }}>
          <Text style={{ color: '#fff', fontSize: 12 }}>Giriş Yap</Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}
