import * as React from 'react';
import { View, Text } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';

import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

import {
  createDrawerNavigator,
} from '@react-navigation/drawer';

import Contact from './screens/Contact.js';
import Cart from './screens/Cart.js';
import Home from './screens/Home.js';
import Login from './screens/Login.js';
import SignUp from './screens/SignUp.js';
import CustomDrawerContent from './components/CustomDrawerContent.js';
import NavigationDrawerOrder from './components/NavigationDrawerOrder.js';
import NavigationDrawerStructure from './components/NavigationDrawerStructure.js';

const Drawer = createDrawerNavigator();

function Layer({navigation}) {
  return (
    <View style={{backgroundColor:'#4bae32', width:'100%',height:50}}>
      <NavigationDrawerStructure
           navigationProps={navigation}
         />
      <Text style={{fontWeight: 'bold', color:'#fff'}}>Trilya</Text>
      <NavigationDrawerOrder
           navigationProps={navigation}
         />
    </View>
    /* options={{
       title: 'Triliya',
 
       headerLeft: () =>
         <NavigationDrawerStructure
           navigationProps={navigation}
         />,
       headerRight: () =>
         <NavigationDrawerOrder
           navigationProps={navigation}
         />,
 
       headerStyle: {
         backgroundColor: '#4bae32', //Set Header color
       },
       headerTintColor: '#fff', //Set Header text color
       headerTitleStyle: {
         fontWeight: 'bold', //Set Header text style
       },
     }}*/
  )
}

function MyDrawer() {
  return (
    <Drawer.Navigator
      initialRouteName="Home"
      drawerContentOptions={{
        activeTintColor: '#4bae32',

      }}
      drawerContent={props => <CustomDrawerContent {...props} />}>
      <Drawer.Screen name="Home" component={Home} />
      <Drawer.Screen name="Sepetim" component={Cart} />
      <Drawer.Screen name="Iletisim" component={Contact} />
      <Drawer.Screen name="Login" component={Login} />
    </Drawer.Navigator>
  );
}

export default function App() {
  return (
    <NavigationContainer>
      <Layer a={navigation} />
      <MyDrawer />
    </NavigationContainer>
  );
}
