import * as React from 'react';
import { View, Image, TouchableOpacity } from 'react-native';
import PropTypes from 'prop-types';
import { NavigationContainer } from '@react-navigation/native';

import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

import {
  createDrawerNavigator,
} from '@react-navigation/drawer';

import Notifications from './screens/Notifications.js';
import Sepetim from './screens/Sepetim.js';
import Home from './screens/Home.js';
import CustomDrawerContent from './components/CustomDrawerContent.js'


const Drawer = createDrawerNavigator();

const NavigationDrawerStructure = (props) => {
  //Structure for the navigatin Drawer
  const toggleDrawer = () => {
    //Props to open/close the drawer
    props.navigationProps.toggleDrawer();
  };

  return (
    <View style={{ flexDirection: 'row' }}>
      <TouchableOpacity onPress={() => toggleDrawer()}>
        {/*Donute Button Image */}
        <Image
          source={require('./images/drawerWhite.png')}
          style={{
            width: 35,
            height: 35,
            marginLeft: 8
          }}
        />
      </TouchableOpacity>
    </View>
  );
}

const NavigationDrawerOrder = (props) => {
  //Structure for the navigatin Drawer
  const toggleDrawer = () => {
    //Props to open/close the drawer
    component = { Sepetim };
  };

  return (
    <View style={{ flexDirection: 'row' }}>
      <TouchableOpacity onPress={() => toggleDrawer()}>
        {/*Donute Button Image */}
        <Image
          source={require('./images/sepet.png')}
          style={{
            width: 45,
            height: 45,
            marginRight: 8
          }}
        />
      </TouchableOpacity>
    </View>
  );
}


class firstScreenStack extends React.Component {
  static propTypes = {
    navigation: PropTypes.shape({
      navigate: PropTypes.func.isRequired,
    }).isRequired,
  };
  render() {
    return (
      <Stack.Navigator initialRouteName="FirstPage">
        <Stack.Screen
          name="FirstPage"
          component={Home}
          options={{
            title: 'Triliya', //Set Header Title

            headerLeft: () =>
              <NavigationDrawerStructure
                navigationProps={navigation}
              />,
            headerRight: () =>
              <NavigationDrawerOrder
                navigationProps={navigation}
              />,

            headerStyle: {
              backgroundColor: '#f4511e', //Set Header color
            },
            headerTintColor: '#fff', //Set Header text color
            headerTitleStyle: {
              fontWeight: 'bold', //Set Header text style
            },
          }}
        />
      </Stack.Navigator>
    );
  }

}

function MyDrawer() {
  return (
    <Drawer.Navigator
      drawerContentOptions={{
        activeTintColor: '#e91e63'
      }}
      drawerContent={props => <CustomDrawerContent {...props} />}>
      <Drawer.Screen name="Ana Sayfa" component={firstScreenStack} />
      <Drawer.Screen name="Bildirimler" component={Notifications} />
      <Drawer.Screen name="Sepetim" component={Sepetim} />
    </Drawer.Navigator>
  );
}

export default function App() {
  const toggleDrawer = () => {
    //Props to open/close the drawer
    props.navigationProps.toggleDrawer();
  };
  return (
    <NavigationContainer>
      <MyDrawer />
    </NavigationContainer>
  );
}
